import React, { useEffect } from "react";

import { useParams } from "react-router-dom";
import axios from "axios";
import { SelectProduct } from "../redux/action/ProductAction";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";

const ProductDetail = () => {
  const { id } = useParams();

  const dispatch = useDispatch();

  console.log(id);

  const detail = useSelector((state) => state.productDetails);

  const { title, price, description, images, c } = detail;

  console.log(detail);

  const fetproductDetail = async () => {
    const response = await axios
      .get(`https://api.escuelajs.co/api/v1/products/${id}`)
      .catch((err) => console.log("err", err));

    dispatch(SelectProduct(response.data));
  };

  useEffect(() => {
    if (id && id !== "") fetproductDetail();
  }, []);

  return (
    <div>
      <h2>{title}</h2>
      <h2>{price}</h2>
      <h3>{description}</h3>
      <img src={images} alt="" />




    </div>
  );
};

export default ProductDetail;
