import React, { useEffect, useState } from "react";

import { useParams } from "react-router-dom";
import axios from "axios";
import { SelectProduct, removeProduct } from "../redux/action/ProductAction";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { incremented } from "../redux/action/ProductAction";
import { decremented } from "../redux/action/ProductAction";

const ProductDetail = () => {
  const { id } = useParams();

  const dispatch = useDispatch();

  const detail = useSelector((state) => state.productDetails);

  const { title, price, description, images } = detail;

  // console.log(detail);

  const number = useSelector((state) => state.Counters);

  const [state_category, setState_category] = useState();

  const fetproductDetail = async () => {
    const response = await axios
      .get(`https://api.escuelajs.co/api/v1/products/${id}`)
      .catch((err) => console.log("err", err));

    dispatch(SelectProduct(response.data));

    const data = response.data;

    setState_category(() => {
      let AllCustumers = data.map((item) => ({
        id: item.id,
        title: item.title,
        category: item.name,
      }));
      return AllCustumers;
    });
  };

  console.log(s)

  useEffect(() => {
    if (id && id !== "") fetproductDetail();

    return () => {
      dispatch(removeProduct());
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id]);

  return (
    <div>
      <h2>{title}</h2>
      <h2>{price}</h2>
      <h3>{description}</h3>
      <img src={images} alt="" />

      <h3>quantity:{number}</h3>

      <button onClick={() => dispatch(incremented(1))}>+</button>
      <button onClick={() => dispatch(decremented(1))}>-</button>
    </div>
  );
};

export default ProductDetail;
